    private void genKeysActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_genKeysActionPerformed
        // Get bits length
        int bitLength = Integer.parseInt(bitLengthInput.getText());
        
        // Initialize RSA
        rsa = new RSA(bitLength);
        
        // Get e and modulus
        PublicKey publicKey = new PublicKey();
        publicKey.setE(rsa.getE());
        publicKey.setModulus(rsa.getModulus());
        
        // Serialize public key
        Serialize.saveConfig("publicKey.dat", publicKey);
        
        // Start server
        ServerProcess server = new ServerProcess(this.jTextArea1, rsa);
    }//GEN-LAST:event_genKeysActionPerformed

